    },
    getGenproProfile: function getGenproProfile(formPayload) {
      dispatch({
        type: GET_GENPROPROFILE,
        formPayload: formPayload
      });
    },
    getProUserDetails: function getProUserDetails(usertoken) {
      dispatch({
        type: GET_PROUSERDETAILS,
        usertoken: usertoken
      });
    },
    getSubscriptiondata: function getSubscriptiondata(usertoken) {
      dispatch({
        type: GET_SUBSCRIPTIONDATA,
        usertoken: usertoken
      });
    }
  };
};

export default connect(mapStateTopProps, mapDispatchToProps)(withRouter(Index));

