		if(this.state.package_price_type.value == 2){
			PlanSym = 'S$';
		}else if(this.state.package_price_type.value == 1){
			PlanSym = '$';
		}
		this.setState({
			packageId: '',
			packagePoints: 0,
			packageAmout: '0.00',
			packageFinalSymbol: PlanSym,
		});

		$(".package_plan_radio").prop("checked", false);
	}

