			pricePlanSym = '$';
			getstripeCurrency = 'USD';
		}else{
			pricePlanType = selectedPlan.topup_amount_sgd;
			pricePlanSym = 'S$';
			getstripeCurrency = 'SGD';
		}

		this.setState({
