	validateForm() {

		const {promo_code} = this.state;

		let errors = {};
      	let formIsValid = true;

		if (!promo_code) {
			formIsValid = false;
			 $('.promoerror').html('<span class="errorspan">Please enter your promocode</span>');
		}else if(promo_code!==''){
			 $('.promoerror').html('');
		}

		this.setState({
			errors: errors
		});

		return formIsValid;
    }

