    getIpData: function getIpData(ipaddress) {
      dispatch({
        type: GET_IPDATA,
        ipaddress: ipaddress
      });
    },
    getPoints: function getPoints() {
      dispatch({
        type: GET_POINTS
      });
    },
    getGeneralProfile: function getGeneralProfile(formPayload) {
      dispatch({
        type: GET_GENERALPROFILE,
        formPayload: formPayload
      });
    },
    getUserProfile: function getUserProfile(usertoken) {
      dispatch({
        type: GET_USERPROFILE,
        usertoken: usertoken
      });
    },
    getPostalAddress: function getPostalAddress(formPayload) {
      dispatch({
        type: GET_POSTALADDRESS,
        formPayload: formPayload
      });
    }
  };
};

export default connect(mapStateTopProps, mapDispatchToProps)(withRouter(Index));

